import React, {Component} from "react";
import AppHeader from "../AppHeader";
import SearchPanel from "../SearchPanel";
import TodoList from "../TodoList";
import ItemStatusFilter from "../ItemStatusFilter";
import ItemAddForm from "../ItemAddForm";

export default class App extends Component {
    maxId = 100;

    createTodoItem = (label) => {
        return {
            label, 
            important: false,
            done: false, 
            id: this.maxId++
        };
    };


    state = {
        todoData: [
            this.createTodoItem("Drink cofee"),
            this.createTodoItem("Make awesome App")
        ]
    };
    
    deleteItem = (id) => {
        this.setState(( {todoData} ) => {
            const indx = todoData.findIndex((el) => el.id === id);

            const before = todoData.slice(0, indx);
            const after = todoData.slice(indx + 1);

            return {
                todoData: [...before, ...after]
            };
        })
    };

    addItem = (text) => {
        this.setState(( {todoData} ) => {
            return {
                todoData: [...todoData, this.createTodoItem(text)]
            };
        });
    };

    onToggleImportant = (id) => {
        console.log("im " + id);

        this.setState(( {todoData} ) => {
            const indx = todoData.findIndex((el) => el.id === id);

            const before = todoData.slice(0, indx);
            const after = todoData.slice(indx + 1);

            var oldItem = {...todoData[indx]};

            console.log("33333 " + !oldItem.important);

            return {
                todoData: [
                    ...before, 
                    { ...oldItem, important: !oldItem.important },
                    ...after]
            };
        })
    };

    onToggleDone = (id) => {
        console.log("Done " + id);
    };
    
    render() {
        return (
            <div>
                <AppHeader todos={this.state.todoData}/>
                <SearchPanel />
                <ItemStatusFilter />
                <TodoList todos = {this.state.todoData}
                 onDeleted = {this.deleteItem}
                 onToggleDone = {this.onToggleDone}
                 onToggleImportant = {this.onToggleImportant}/>
                 <ItemAddForm onAdded = {this.addItem}/>
            </div>
        )
    }
};
