import React, {Component} from "react"
import "./ItemAddForm.css"

export default class ItemAddForm extends Component {
    state = {
        needDone: ""
    };

    onLabelChange = (e) => {
        this.setState(() => {
            return ({
                needDone: e.target.value
            })
        });
    };

    render() {
        return (
            <form className="item-add-form d-flex">
                <input type="text" onChange={this.onLabelChange} 
                    className="form-control" placeholder="What needs to be done" />
                <button className="btn btn-outline-secondary tem-add-form"
                    onClick={() => this.props.onAdded(this.state.needDone)}>
                    Add
                </button>
            </form>
        )
    }
};
