import React, {Component} from "react"

export default class ItemStatusFilter extends Component {
    buttons = [
        { name: 'All', label: 'All' },
        { name: 'Active', label: 'Active' },
        { name: 'Done', label: 'Done' }
    ];

    render() {
        const buttons = this.buttons.map(( {name, label} ) => {
            return <button type="button" className="btn" name={name} > 
                {label}
            </button>
        });

        return(
            <div>
                <button type="button" className = "btn btn-outline-secondary">All</button>
                <button type="button" className = "btn btn-info">Active</button>
                <button type="button" onClick={this.props.s}
                className = "btn btn-outline-secondary">Done</button>
            </div>
        );
    };
};
